mapper.select:
SELECT sid, uid, title, description, type, state, create_time, update_time, open_time, fill_num, time_limit
FROM survey
WHERE sid = #{sid}

mapper.update:
update survey
title = #{title}, description = #{description}, type = #{type,typeHandler=org.example.handler.type.SurveyTypeHandler}, state = #{state,typeHandler=org.example.handler.type.SurveyStateHandler}, update_time = #{updateTime}, time_limit = #{timeLimit}
WHERE sid = #{sid}
